import {
    StyleSheet,
    Text,
    View,
    SafeAreaView,
    Picker,
    TextInput,
    Button,
    Pressable,
  } from "react-native";
  import React, { useState, useEffect } from "react";
  import { useNavigate, useParams, useLocation } from "react-router-dom";
  import axios from "axios";

function GetP_id() {
    var c = 0;
    var index = 0;
    var path = location.pathname;
    for (var i = 0; i < path.length; i++) {
      if (path.charAt(i) == "/") {
        c++;
      }
      if (c == 3) {
        index = i;
        break;
      }
      if (c == 2) {
        index = path.length;
      }
    }
    return path.substring(12, index);

}

function ReviewDetails(props) {
    const [prismScore, setPrismScore] = useState("");
    const [crsScore,setCRSScore] = useState("");
    const [oiScore, setOiScore] = useState("");
    const [gcsScore,setGcsScore] = useState("");
    const [psofaScore,setPsofaScore] = useState("");

    function GetAssessment() {
        axios
          .get("http://localhost:4000/get/Prism/" + GetP_id())
          .then((response) => {
            if ((response.data) != null && response.data.length==1) {
              setPrismScore(response.data[0].Score)
            }
            else {
              alert("Unable to get previous data!");

            }
          })
          .catch(function (error) {
            console.log(error);
          });

        axios
        .get("http://localhost:4000/get/CRS/" + GetP_id())
        .then((response) => {
        if ((response.data) != null && response.data.length==1) {
            setCRSScore(response.data[0].Score)
        }
        else {
            alert("Unable to get previous data!");

        }
        })
        .catch(function (error) {
        console.log(error);
        });
        axios
        .get("http://localhost:4000/get/GCS/" + GetP_id())
        .then((response) => {
        if ((response.data) != null && response.data.length==1) {
            setGcsScore(response.data[0].Score)
        }
        else {
            alert("Unable to get previous data!");

        }
        })
        .catch(function (error) {
        console.log(error);
        });
        axios
        .get("http://localhost:4000/get/OI/" + GetP_id())
        .then((response) => {
        if ((response.data) != null && response.data.length==1) {
            setOiScore(response.data[0].Score)
        }
        else {
            alert("Unable to get previous data!");

        }
        })
        .catch(function (error) {
        console.log(error);
        });
        axios
        .get("http://localhost:4000/get/PSofa/" + GetP_id())
        .then((response) => {
        if ((response.data) != null && response.data.length==1) {
            setPsofaScore(response.data[0].Score)
        }
        else {
            alert("Unable to get previous data!");

        }
        })
        .catch(function (error) {
        console.log(error);
        });
      }
      useEffect(() => {
        GetAssessment();
      }, []);

    const { patient } = props;
    return (
        <SafeAreaView style={styles.card}>
            <text>Prism Score: {prismScore}</text>
            <text>CRS Score: {crsScore}</text>
            <text>OI Score: {oiScore}</text>
            <text>GCS Score: {gcsScore}</text>
            <text>psofaScore: {}</text>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    card: {
        borderRadius: 6,
        elevation: 3,
        backgroundColor: '#fff',
        shadowOffset: { width: 1, height: 1 },
        shadowColor: '#333',
        shadowOpacity: 0.3,
        shadowRadius: 2,
        marginHorizontal: 4,
        marginVertical: 6,
    },
    cardContent: {
        marginHorizontal: 18,
        marginVertical: 8,
    }
});

export default ReviewDetails;
