import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from "react-router-dom";
import {
    Alert,
    Button,
    ImageBackground,
    StyleSheet,
    Modal,
    Text,
    Pressable,
    View,
    SafeAreaView,
    TextInput
} from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import ReviewDetails from './ReviewDetails';
import axios from 'axios'

const Reminder = (props) => {
    const [GCS_List, setGCS_List] = useState([]);
    const [patients, setPatients] = useState([]);
    function GetLists(){
        axios
      .get("http://localhost:4000/get/GCS")
      .then((response) => {
        if ((response.data) != null ) {
            console.log(response.data)
            let gcs=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(response.data[i].Time )// condition
                {
                    gcs.push(response.data[i])
                }
            }
            setGCS_List(gcs)
        }
        else {
          alert("Unable to get GCS data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
    }
    useEffect(() => {
        if (localStorage.getItem('user_data') == null) {
            alert("Unauthorised Access")
            navigate("/")
        }

        GetLists();
    }, []);

    const navigate = useNavigate();

    const [modalVisible, setModalVisible] = useState(false);
    const [name, onChangeName] = useState("");
    // const [age, onChangeAge] = useState("")
    const [gender, onChangeGender] = useState("")
    const [patientIp, onChangePatientIp] = useState("")
    const [date, onChangeDate] = useState("");
    const [time, onChangeTime] = useState("");
    const [open, setOpen] = useState(false);

    const resetInputs = () => {
        onChangeName("");
        // onChangeAge("");
        onChangeGender("");
        onChangePatientIp("");
        onChangeDate("");
        onChangeTime("");
    };

    // for fuzzy search
    const [searchData, setSearchData] = useState(patients);
    const searchItem = (query) => {
        if (!query) {
            setPatients(patient_List);
            return;
        }
        const finalResult = [];
        patient_List.forEach((item) => {
            if (
                item.P_ID.indexOf(query) !== -1
            ) {
                finalResult.push(item);
            }
        });
        setPatients(finalResult);
    };

    return (
        <SafeAreaView >
            <View style={styles.container}>

                <View style={styles.container1}>
                    <Icon
                        style={styles.searchIcon}
                        name="search"
                        size={20}
                        color="#56BF9C"
                    />
                    <TextInput
                        inlineImageLeft='search_icon'
                        style={styles.buttonfuzzySearch}
                        type="search"
                        placeholder="  Search Patient By ID"
                        onChange={(e) => searchItem(e.target.value)}
                    />
                </View>
            </View>


            {patients.map((patient) => {
                return < ReviewDetails key={patient.P_ID} patient={patient} />
            })
            }
        </SafeAreaView >
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingTop: 5,
        paddingBottom: 5,
    },
    container1: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'flex-end',
        // paddingTop: 5,
        // paddingBottom: 5,
    },
    centeredView: {
        flex: 1,
        margin: 50,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 15,
        alignItems: "center",
        justifyContent: "center",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 5
        },
        shadowOpacity: 0.75,
        shadowRadius: 4,
        elevation: 15
    },
    modalView: {
        margin: 20,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 35,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5
    },
    buttonOpen: {
        backgroundColor: "#56BF9C",
    },
    buttonOpen1: {
        backgroundColor: "#56BF9C",
        padding: 4,
        // borderRadius: 30
    },
    input: {
        height: 40,
        margin: 12,
        borderWidth: 1,
        padding: 5,
    },
    buttonfuzzySearch: {
        borderWidth: 2,
        borderRadius: 8,
        borderColor: "#56BF9C",
        strokeWidth: "1"
    },
    searchIcon: {
        padding: 5
    }
});

export default Reminder;
