import { StyleSheet, Text, View,SafeAreaView, Picker, TextInput, Button, Pressable } from "react-native";
import React, { useState } from "react";

const OI = () => {
  const [MAP, setMAP] = useState("");
  const [fio2, setFio2] = useState("");
  const [pao2, setPao2] = useState("");

  let score = 0;
  let flag = 0;
  let pf = 0
  if (Number(MAP) > 0 && Number(fio2) > 0 && Number(pao2) > 0) {
    flag = 1;
    pf = Number(pao2)/Number(fio2)
    score = Number(MAP)/pf;
  }

  const onSubmit = (event) => {
    const newOI = {
      MAP:MAP,
      fio2:fio2,
      pao2:pao2
    }
  }
  return (
    <SafeAreaView>
      <Text style={styles.prism}>Oxygenation Index(OI)</Text>
      <View style={[styles.container1]}>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>MAP: </Text>
          <TextInput
            style={styles.input}
            placeholder="MAP"
            onChangeText={setMAP}
            value={MAP}
          />
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>FiO2: </Text>
          <TextInput
            style={styles.input}
            placeholder="FiO2"
            onChangeText={setFio2}
            value={fio2}
          />
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>PaO2: </Text>
          <TextInput
            style={styles.input}
            placeholder="PaO2"
            onChangeText={setPao2}
            value={pao2}
          />
        </View>
      </View>
      {flag == 1 && (
        <View style={[styles.container2]}>
          <View>
            <Text style={styles.assessment}>
              pf Ratio = {pf}
            </Text>
            {pf >200 && pf <=300 &&
              <Text style={styles.assessment}>
              Acute Lung Injury (ALI) with Mild Acute Respiratory Distress syndrome (ARDS)
              </Text>
            }
            {pf >100 && pf <=200 &&
              <Text style={styles.assessment}>
              Moderate Acute Respiratory Distress syndrome (ARDS)
              </Text>
            }
            {pf <=100 &&
              <Text style={styles.assessment}>
               Severe Acute Respiratory Distress syndrome (ARDS)
              </Text>
            }
            <Text style={styles.assessment}>
              OI = {score}
            </Text>
            {score < 4 && 
            <Text style={styles.assessment}>
              Severity: At risk for pediatric ARDS
            </Text>
            }
            {score >= 4 && score < 8 && 
            <Text style={styles.assessment}>
              Severity: Mild pediatric ARDS
            </Text>
            }
            {score >=8 && score <16 && 
            <Text style={styles.assessment}>
              Severity: Moderate pediatric ARDS
            </Text>
            }
            {score >=16 && 
            <Text style={styles.assessment}>
              Severity: severe pediatric ARDS
            </Text>
            }
          </View>
        </View>
      )}
      <Pressable style = {styles.submit}>
        <Button
          title="Submit"

          onPress={onSubmit}
        >
          Submit
        </Button>
      </Pressable>
    </SafeAreaView>
  );
};
export default OI;

const styles = StyleSheet.create({
  prism: {
    fontSize: 50,
    fontWeight: "bold",
    textAlign: "center",
  },
  assessment: {
    marginTop: 20,
    fontSize: 30,
    fontWeight: "bold",
  },
  dropContainer: {
    flexDirection: "row",
    marginBottom: 20,
    marginTop: 10,
  },
  dropdownContent: {
    fontSize: 20,
    marginTop: 10,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    width: 200,
  },
  text: {
    fontWeight: "bold",
  },
  container1: {
    backgroundColor: "#f0ffff",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container2: {
    backgroundColor: "#fffacd",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container3: {
    backgroundColor: "#ffb6c1",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container4: {
    backgroundColor: "#f5f5dc",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container5: {
    backgroundColor: "#dda0dd",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  subscore: {
    fontWeight: "bold",
    borderWidth: 1,
    backgroundColor: "#fff",
    margin: 10,
    padding: 4,
    fontSize: 20,
  },
  submit: {
    borderWidth: 2,
    backgroundColor:"#56BF9C",
    margin: 10,
    padding: 7,
    marginLeft: 800,
    width: 90,
  }
});
