import { StyleSheet, Text, View,SafeAreaView, Picker, TextInput } from "react-native";
import React, { useState } from "react";

const Gcs = () => {
  const [age, setAge] = useState("");
  const [eye, setEye] = useState(0);
  const [motor, setMotor] = useState(0);
  const [verbal, setVerbal] = useState(0);

  let score = Number(eye) + Number(motor) + Number(verbal);
  let flag = 0;
  if (Number(eye) > 0 && Number(motor) > 0 && Number(verbal) > 0) {
    flag = 1;
  }
  return (
    <SafeAreaView>
      <Text style={styles.prism}>GlassGo Coma Scale (GCS)</Text>
      <View style={styles.dropContainer}>
        <Text style={styles.dropdownContent}>Age: </Text>
        <TextInput
          style={styles.input}
          placeholder="Age"
          onChangeText={setAge}
          value={age}
        />
      </View>
      <View style={[styles.container1]}>
        {Number(age) > 0 && Number(age) < 1 && (
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>i.Eye Opening: </Text>
            <Picker
              selectedValue={eye}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setEye(itemValue)}
            >
              <Picker.Item label="" value={0} />
              <Picker.Item label="Spontaneously" value={4} />
              <Picker.Item label="To shout" value={3} />
              <Picker.Item label="To pain" value={2} />
              <Picker.Item label="No response" value={1} />
            </Picker>
          </View>
        )}
        {Number(age) >= 1 && (
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>i.Eye Opening: </Text>
            <Picker
              selectedValue={eye}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setEye(itemValue)}
            >
              <Picker.Item label="" value={0} />
              <Picker.Item label="Spontaneously" value={4} />
              <Picker.Item label="To verbal Command" value={3} />
              <Picker.Item label="To pain" value={2} />
              <Picker.Item label="No response" value={1} />
            </Picker>
          </View>
        )}
        {Number(age) > 0 && Number(age) < 1 && (
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>ii.Best Motor Response: </Text>
            <Picker
              selectedValue={motor}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setMotor(itemValue)}
            >
              <Picker.Item label="" value={0} />
              <Picker.Item label="Localises Pain" value={5} />
              <Picker.Item label="Flexion-withdrawl" value={4} />
              <Picker.Item
                label="Flexion-abnormal (decorticate rigidity)"
                value={3}
              />
              <Picker.Item label="Extension (decerbrate rigidity)" value={2} />
              <Picker.Item label="No response" value={1} />
            </Picker>
          </View>
        )}
        {Number(age) >= 1 && (
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>ii.Best Motor Response: </Text>
            <Picker
              selectedValue={motor}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setMotor(itemValue)}
            >
              <Picker.Item label="" value={0} />
              <Picker.Item label="Obeys" value={6} />
              <Picker.Item label="Localises Pain" value={5} />
              <Picker.Item label="Flexion-withdrawl" value={4} />
              <Picker.Item
                label="Flexion-abnormal (decorticate rigidity)"
                value={3}
              />
              <Picker.Item label="Extension (decerbrate rigidity)" value={2} />
              <Picker.Item label="No response" value={1} />
            </Picker>
          </View>
        )}
        {Number(age) > 0 && Number(age) < 2 && (
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>
              iii.Best Verbal Response:{" "}
            </Text>
            <Picker
              selectedValue={verbal}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setVerbal(itemValue)}
            >
              <Picker.Item label="" value={0} />
              <Picker.Item label="Smiles,coos,cries appropriately" value={5} />
              <Picker.Item label="Cries" value={4} />
              <Picker.Item
                label="Inappropriate crying and/or screaming"
                value={3}
              />
              <Picker.Item label="Grunts" value={2} />
              <Picker.Item label="No response" value={1} />
            </Picker>
          </View>
        )}
        {Number(age) >= 2 && Number(age) < 5 && (
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>
              iii.Best Verbal Response:{" "}
            </Text>
            <Picker
              selectedValue={verbal}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setVerbal(itemValue)}
            >
              <Picker.Item label="" value={0} />
              <Picker.Item label="Appropriate words and phrases" value={5} />
              <Picker.Item label="Inappropriate words" value={4} />
              <Picker.Item label="Cries and/or screams" value={3} />
              <Picker.Item label="Grunts" value={2} />
              <Picker.Item label="No response" value={1} />
            </Picker>
          </View>
        )}
        {Number(age) >= 5 && (
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>
              iii.Best Verbal Response:{" "}
            </Text>
            <Picker
              selectedValue={verbal}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setVerbal(itemValue)}
            >
              <Picker.Item label="" value={0} />
              <Picker.Item label="Oriented and converses" value={5} />
              <Picker.Item label="Disoriented and converses" value={4} />
              <Picker.Item label="Inappropriate words" value={3} />
              <Picker.Item label="Incomprehensible sounds" value={2} />
              <Picker.Item label="No response" value={1} />
            </Picker>
          </View>
        )}
      </View>
      {flag == 1 && (
        <View style={[styles.container2]}>
          <View>
            <Text style={styles.assessment}>
              Score: GCS{score} = E{eye}V{verbal}M{motor}
            </Text>
            {score >= 3 && score <= 8 && (
              <Text style={styles.assessment}>Severity: Severe</Text>
            )}
            {score >= 9 && score <= 12 && (
              <Text style={styles.assessment}>Severity: Moderate</Text>
            )}
            {score >= 13 && score <= 15 && (
              <Text style={styles.assessment}>Severity: Mild</Text>
            )}
          </View>
        </View>
      )}
    </SafeAreaView>
  );
};

export default Gcs;

const styles = StyleSheet.create({
  prism: {
    fontSize: 50,
    fontWeight: "bold",
    textAlign: "center",
  },
  assessment: {
    marginTop: 20,
    fontSize: 30,
    fontWeight: "bold",
  },
  dropContainer: {
    flexDirection: "row",
    marginBottom: 20,
    marginTop: 10,
  },
  dropdownContent: {
    fontSize: 20,
    marginTop: 10,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    width: 200,
  },
  text: {
    fontWeight: "bold",
  },
  container1: {
    backgroundColor: "#f0ffff",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container2: {
    backgroundColor: "#fffacd",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container3: {
    backgroundColor: "#ffb6c1",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container4: {
    backgroundColor: "#f5f5dc",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container5: {
    backgroundColor: "#dda0dd",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  subscore: {
    fontWeight: "bold",
    borderWidth: 1,
    backgroundColor: "#fff",
    margin: 10,
    padding: 4,
    fontSize: 20,
  },
});
