import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Picker,
  TextInput,
  Button,
  Pressable,
} from "react-native";
import React, { useState, useEffect } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import axios from "axios";

function GetP_id() {
  var c = 0;
  var index = 0;
  var path = location.pathname;
  for (var i = 0; i < path.length; i++) {
    if (path.charAt(i) == "/") {
      c++;
    }
    if (c == 3) {
      index = i;
      break;
    }
    if (c == 2) {
      index = path.length;
    }
  }
  return path.substring(12, index);
}

function ReviewDetails(props) {
  const [prismScore, setPrismScore] = useState("");
  const [crsScore, setCRSScore] = useState("");
  const [oiScore, setOiScore] = useState("");
  const [oiSeverity, setOiSeverity] = useState("");
  const [pfScore, setPfScore] = useState("");
  const [pfSeverity, setPfSeverity] = useState("");
  const [gcsScore, setGcsScore] = useState("");
  const [gcsSeverity, setGcsSeverity] = useState("");
  const [psofaScore, setPsofaScore] = useState("");

  function GetAssessment() {
    axios
      .get("http://localhost:4000/get/Prism/" + GetP_id())
      .then((response) => {
        setPrismScore(response.data[0].Score);
      })
      .catch(function (error) {
        console.log(error);
      });

    axios
      .get("http://localhost:4000/get/CRS/" + GetP_id())
      .then((response) => {
        setCRSScore(response.data[0].Score);
      })
      .catch(function (error) {
        console.log(error);
      });
    axios
      .get("http://localhost:4000/get/GCS/" + GetP_id())
      .then((response) => {
        setGcsScore(response.data[0].Score);
        setGcsSeverity(response.data[0].Severity);
      })
      .catch(function (error) {
        console.log(error);
      });
    axios
      .get("http://localhost:4000/get/OI/" + GetP_id())
      .then((response) => {
        setOiScore(response.data[0].OI_score);
        setOiSeverity(response.data[0].OI_severity);
        setPfScore(response.data[0].PF);
        setPfSeverity(response.data[0].PF_severity);
      })
      .catch(function (error) {
        console.log(error);
      });
    axios
      .get("http://localhost:4000/get/PSofa/" + GetP_id())
      .then((response) => {
        setPsofaScore(response.data[0].Score);
      })
      .catch(function (error) {
        console.log(error);
      });
  }
  useEffect(() => {
    GetAssessment();
  }, []);

  const { patient } = props;
  return (
    <SafeAreaView style={styles.card}>
        <Text style={styles.prism}>PRISM III</Text>
      <View style={[styles.container1]}>
        <View>
          <Text style={styles.assessment}>
            1.Cardiovascular and Neurological
          </Text>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>i.Heart Rate: </Text>

        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>ii.GCS: </Text>
          <TextInput style={styles.input} onChangeText={setGcs} value={gcs} />
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>iii.Temperature: </Text>
          <Picker
            selectedValue={temperature}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setTemperature(itemValue)}
          >
            <Picker.Item lable="" value="-" />
            <Picker.Item label="<33" value="03<33" />
            <Picker.Item label="33-40" value="0033-40" />
            <Picker.Item label=">40" value="03>40" />
          </Picker>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>iv.Pupillary Response: </Text>
          <Picker
            selectedValue={pupil}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setPupil(itemValue)}
          >
            <Picker.Item lable="" value="-" />
            <Picker.Item label="Both reactive" value="00Both reactive" />
            <Picker.Item
              label="One reactive and (1 fixed and >3mm)"
              value="07One reactive and (1 fixed and >3mm)"
            />
            <Picker.Item
              label="Both fixed and both > 3mm"
              value="11Both fixed and both > 3mm"
            />
          </Picker>
        </View>
        <Text style={styles.subscore}>
          Cardiovascular and Neurological subscore: {cardScore}
        </Text>
      </View>
      <text>Prism Score: {prismScore}</text>
      <text>CRS Score: {crsScore}</text>
      <text>OI Score: {oiScore}</text>
      <text>GCS Score: {gcsScore}   GCS Severity: {gcsSeverity}</text>
      <text>PSofa Score: {psofaScore}</text>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
    prism: {
        fontSize: 50,
        fontWeight: "bold",
        textAlign: "center",
      },
      assessment: {
        marginTop: 20,
        fontSize: 30,
        fontWeight: "bold",
      },
      dropContainer: {
        flexDirection: "row",
        marginBottom: 20,
        marginTop: 10,
      },
      dropdownContent: {
        fontSize: 20,
        marginTop: 10,
      },
      input: {
        height: 40,
        margin: 12,
        borderWidth: 1,
        padding: 10,
        width: 200,
      },
      text: {
        fontWeight: "bold",
      },
      container1: {
        backgroundColor: "#f0ffff",
        paddingLeft: 20,
        borderWidth: 2,
        margin: 5,
      },
      container2: {
        backgroundColor: "#fffacd",
        paddingLeft: 20,
        borderWidth: 2,
        margin: 5,
      },
      container3: {
        backgroundColor: "#ffb6c1",
        paddingLeft: 20,
        borderWidth: 2,
        margin: 5,
      },
      container4: {
        backgroundColor: "#f5f5dc",
        paddingLeft: 20,
        borderWidth: 2,
        margin: 5,
      },
      container5: {
        backgroundColor: "#dda0dd",
        paddingLeft: 20,
        borderWidth: 2,
        margin: 5,
      },
      subscore: {
        fontWeight: "bold",
        borderWidth: 1,
        backgroundColor: "#fff",
        margin: 10,
        padding: 4,
        fontSize: 20,
      },
      submit: {
        borderWidth: 2,
        backgroundColor: "#56BF9C",
        margin: 10,
        padding: 7,
        width: 90,
      },
});

export default ReviewDetails;
