import { StyleSheet, Text, View,SafeAreaView, Picker, TextInput, Button, Pressable } from "react-native";
import React, { useState, useEffect } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import axios from 'axios';

function GetP_id() {

  var c = 0;
  var index = 0
  var path = location.pathname
  for (var i = 0; i < path.length; i++) {
    if (path.charAt(i) == '/') {
      c++;
    }
    if (c == 3) {
      index = i
      break;
    }
    if (c == 2) {
      index = path.length;
    }
  }
  return path.substring(12, index)
}
const Psofa = () => {
  const [pao2, setPao2] = useState("-");
  const [spo2,setSpo2] = useState("-");
  const [platelet,setPlatelet] = useState("-")
  const [bilirubin,setBilirubin] = useState("-")
  const [gcs,setGcs] = useState("-")
  const [renal,setRenal] = useState("-")
  const [age,setAge] = useState("-")
  const [selectFlag, setSelectFlag] = useState("-")
  const [cardio,setCardio] = useState("-")
  const [vaso,setVaso] = useState("-")
  const [respiratory,setRespiratory] = useState("-")
  const [c, setC] = useState(1);
  const location = useLocation();
  let score;
  let cardioScore = cardio;
  if (Number(vaso) > 1){
    cardioScore = vaso
  }
  let flag = 0
  if((respiratory.substring(0,1)) != '-' && platelet.substring(0,1) != '-' && bilirubin.substring(0,1) != '-'  && gcs.substring(0,1) != '-' && renal.substring(0,1) != '-' && cardioScore.substring(0,1) != '-'){
      flag = 1
      score = Number(respiratory.substring(0,1))+Number(platelet.substring(0,1))+Number(bilirubin.substring(0,1))+Number(gcs.substring(0,1))+Number(renal.substring(0,1))+Number(cardioScore.substring(0,1))
  }

  const onClickAdd = (event) => {
    if (flag == 1) {
      setC(0);
      const newPSofa = {
        P_id: GetP_id(),
        a: pao2,
        b: gcs,
        c: temperature,
        d: pupil,
        e: cardScore,
        f: acidpH,
        g: alkalinepH,
        h: pco2,
        i: totalco2,
        j: pao2,
        k: bloodScore,
        l: plasma,
        m: serum,
        n: creatinine,
        o: urea,
        p: bioScore,
        q: wbc,
        r: platelet,
        s: ptt,
        t: haemoScore,
        u: score,
      };

      // console.log(newCrs)
      axios
        .post("http://localhost:4000/add/PSofa", newPSofa)
        .then((response) => {
          if (response.data != null) {
            alert("Added");
          } else {
            alert("Unable to add!");
          }
          // console.log(response.data);
        })
        .catch(function (error) {
          console.log(error);
        });
    } else {
      alert("Fill all the details");
    }
  };

  function GetAssessment() {
    axios
      .get("http://localhost:4000/get/PSofa/" + GetP_id())
      .then((response) => {
        if ((response.data) != null && response.data.length==1) {
          console.log(response.data[0])

          setHeartRate(response.data[0].Card_HR)
          setGcs(response.data[0].Card_GCS)
          setTemperature(response.data[0].Card_Temp)
          setPupil(response.data[0].Card_Pup)
          setAcidpH(response.data[0].AB_AcidPH)
          setAlkalinepH(response.data[0].AB_AlkPH)
          setPco2(response.data[0].AB_PCO2)
          setTotalco2(response.data[0].AB_TCO2)
          setPao2(response.data[0].AB_PaO2)
          setPlasma(response.data[0].BT_PG)
          setSerum(response.data[0].BT_SP)
          setCreatinine(response.data[0].BT_SC)
          setUrea(response.data[0].BT_BUN)
          setWbc(response.data[0].HT_WBC)
          setPlatelet(response.data[0].HT_PC)
          setPtt(response.data[0].HT_PTT)
        }
        else {
          alert("Unable to get previous data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }
  useEffect(() => {
    // console.log("api")
    GetAssessment();
  }, []);


  return (
    <SafeAreaView>
      <Text style={styles.prism}>
        Pediatric Sequential Organ Failure Assessment (P SOFA) Score
      </Text>
      <View style={[styles.container1]}>
        <View>
          <Text style={styles.assessment}>1.Respiratory</Text>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>Select: </Text>
          <Picker
            selectedValue={selectFlag}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setSelectFlag(itemValue)}
          >
            <Picker.Item label="-" value={-1} />
            <Picker.Item label="PaO2:FiO2" value={2} />
            <Picker.Item label="SpO2:FiO2" value={1} />
          </Picker>
        </View>
        {Number(selectFlag) == 2 && (
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>PaO2:FiO2: </Text>
          <Picker
            selectedValue={respiratory}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setRespiratory(itemValue)}
          >
            <Picker.Item label="-" value={-1} />
            <Picker.Item label=">=400" value={0} />
            <Picker.Item label="300-399" value={1} />
            <Picker.Item label="200-299" value={2} />
            <Picker.Item label="100-199 with respiratory support" value={3} />
            <Picker.Item label="<100 with respiratory support" value={4} />
          </Picker>
        </View>
        )}
        {Number(selectFlag) == 1 && (
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>SpO2:FiO2: </Text>
          <Picker
            selectedValue={respiratory}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setRespiratory(itemValue)}
          >
            <Picker.Item label="-" value={-1} />
            <Picker.Item label=">=292" value={0} />
            <Picker.Item label="264-291" value={1} />
            <Picker.Item label="221-264" value={2} />
            <Picker.Item label="148-220 with respiratory support" value={3} />
            <Picker.Item label="<148 with respiratory support" value={4} />
          </Picker>
        </View>
        )}
        {Number(respiratory)>=0 && (<Text style={styles.subscore}>
          Subscore:{" "}
          {Number(respiratory)}
        </Text>
        )}
      </View>
      <View style={[styles.container2]}>
        <View>
          <Text style={styles.assessment}>2.Coagulation</Text>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>Platelet Count: </Text>
          <Picker
            selectedValue={platelet}
            style={{ height: 50, width: 150 }}
