import { StyleSheet, Text, View, SafeAreaView, Picker, TextInput, Button, Pressable } from 'react-native'
import React, { useState } from 'react'

const Crs = () => {
    const [Respiratory, setRespiratory] = useState();
    const [auscultation, setAuscultation] = useState();
    const [muscles, setMuscles] = useState();
    const [mental, setMental] = useState();
    const [room, setRoom] = useState();
    const [color, setColor] = useState();

    let score = Number(Respiratory)+ Number(auscultation)+ Number(muscles) + Number(mental)+ Number(room)+ Number(color);
    let flag = 0
    if (Number(Respiratory) >= 0 && Number(auscultation) >= 0 && Number(muscles) >= 0 && Number(mental) >=0 && Number(room) >= 0 && Number(color) >=0){
      flag = 1;
    }

    const onSubmit = (event) => {

    }
    return (
      <SafeAreaView> 
        <Text style={styles.prism}>Clinical Respiratory Score (CRS)</Text>
        <View style={[styles.container1]}>
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>i.Respiratory Rate: </Text>
            <Picker
              selectedValue={Respiratory}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setRespiratory(itemValue)}
            >
              <Picker.Item label="" value={-1} />
              <Picker.Item label="Age 1-5 years: <30" value={0} />
              <Picker.Item label="Age 1-5 years: 30-40" value={1} />
              <Picker.Item label="Age 1-5 years: >40" value={2} />
              <Picker.Item label="Age > 5 years: <20" value={0} />
              <Picker.Item label="Age > 5 years: 20-30" value={1} />
              <Picker.Item label="Age > 5 years: >30" value={2} />
            </Picker>
          </View>
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>ii.Auscultation: </Text>
            <Picker
              selectedValue={auscultation}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setAuscultation(itemValue)}
            >
              <Picker.Item label="" value={-1} />              
              <Picker.Item label="Good air movement, Expiratory scattered wheezing or loose rales/crackles" value={0} />
              <Picker.Item label="Depressed air movement, inspiratory and expiratory wheezes or rales/crackles" value={1} />
              <Picker.Item label="Diminished or absent breath sounds, severe wheezing or rales/crackles or marked prolonged expiration" value={3} />
            </Picker>
          </View>
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>iii. Use of Accessory Muscles: </Text>
            <Picker
              selectedValue={muscles}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setMuscles(itemValue)}
            >
              <Picker.Item label="" value={-1} />
              <Picker.Item label="Mild to no use of accessory muscles, Mild to no retractions or nasal flaring on inspiration" value={0} />
              <Picker.Item
                label="Moderate intercostal retractions, mild to moderate use of accessory muscles, nasal flaring"
                value={1}
              />
              <Picker.Item label="Severe intercostal and substernal retractions, nasal flaring" value={2} />
            </Picker>
          </View>
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>iv. Mental status: </Text>
            <Picker
              selectedValue={mental}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setMental(itemValue)}
            >
              <Picker.Item label="" value={-1} />
              <Picker.Item label="Normal to Mildly irratable" value={0} />
              <Picker.Item
                label="Irritable, agitated restless"
                value={1}
              />
              <Picker.Item label="Lethargic" value={2} />
            </Picker>
          </View>
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>v.Room Air SpO2: </Text>
            <Picker
              selectedValue={room}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setRoom(itemValue)}
            >
              <Picker.Item label="" value={-1} />
              <Picker.Item label=">95%" value={0} />
              <Picker.Item
                label="90-95%"
                value={1}
              />
              <Picker.Item label="<90%" value={2} />
            </Picker>
          </View>
          <View style={styles.dropContainer}>
            <Text style={styles.dropdownContent}>vi.Color: </Text>
            <Picker
              selectedValue={color}
              style={{ height: 50, width: 150 }}
              onValueChange={(itemValue, itemIndex) => setColor(itemValue)}
            >
              <Picker.Item label="" value={-1} />
              <Picker.Item label="Normal" value={0} />
              <Picker.Item
                label="Pale to Normal"
                value={1}
              />
              <Picker.Item label="Cyanotic, Dusky" value={2} />
            </Picker>
          </View>
        </View>
        {flag == 1 && (
        <View style={[styles.container5]}>
          <View>
            <Text style={styles.assessment}>
              Score: {score}
            </Text>
            {score <= 3 && (
              <Text style={styles.assessment}>Severity: Mild</Text>
            )}
            {score >= 4 && score <= 7 && (
              <Text style={styles.assessment}>Severity: Moderate</Text>
            )}
            {score >= 8 && score <= 12 && (
              <Text style={styles.assessment}>Severity: Severe</Text>
            )}
          </View>
        </View>
      )}
      </SafeAreaView>
    );
}

export default Crs

const styles = StyleSheet.create({
    prism: {
      fontSize: 50,
      fontWeight: "bold",
      textAlign: "center",
    },
    assessment: {
      marginTop: 20,
      fontSize: 30,
      fontWeight: "bold",
    },
    dropContainer: {
      flexDirection: "row",
      marginBottom: 20,
      marginTop: 10,
    },
    dropdownContent: {
      fontSize: 20,
      marginTop: 10,
    },
    input: {
      height: 40,
      margin: 12,
      borderWidth: 1,
      padding: 10,
      width: 200,
    },
    text: {
      fontWeight: "bold",
    },
    container1: {
      backgroundColor: "#f0ffff",
      paddingLeft: 20,
      borderWidth: 2,
      margin: 5,
    },
    container2: {
      backgroundColor: "#fffacd",
      paddingLeft: 20,
      borderWidth: 2,
      margin: 5,
    },
    container3: {
      backgroundColor: "#ffb6c1",
      paddingLeft: 20,
      borderWidth: 2,
      margin: 5,
    },
    container4: {
      backgroundColor: "#f5f5dc",
      paddingLeft: 20,
      borderWidth: 2,
      margin: 5,
    },
    container5: {
      backgroundColor: "#dda0dd",
      paddingLeft: 20,
      borderWidth: 2,
      margin: 5,
    },
    subscore: {
      fontWeight: "bold",
      borderWidth: 1,
      backgroundColor: "#fff",
      margin: 10,
      padding: 4,
      fontSize: 20,
    },
  });
