import { StyleSheet, Text, View,SafeAreaView, Picker, TextInput } from "react-native";
import React, { useState } from "react";

const Api = () => {
  const [sPrimary, setSPrimary] = useState("");
  const [sAsthma, setSAsthma] = useState("");
  const [sDermatitis, setSDermatitis] = useState("");
  const [sCold, setSCold] = useState("");
  const [sCirculation, setSCirculation] = useState("");
  const [sRhinitis, setSRhinitis] = useState("");
  const [mPrimary, setMPrimary] = useState("");
  const [mAsthma, setMAsthma] = useState("");
  const [mDermatitis, setMDermatitis] = useState("");
  const [mAeroallergen, setMAeroallergen] = useState("");
  const [mCold, setMCold] = useState(0);
  const [creatinine, setCreatinine] = useState(0);
  const [urea, setUrea] = useState(0);
  const [wbc, setWbc] = useState(0);
  const [platelet, setPlatelet] = useState(0);
  const [ptt, setPtt] = useState(0);

  let score;
  return (
    <SafeAreaView>
      <Text style={styles.prism}>PRISM III</Text>
      <View style={[styles.container1]}>
        <View>
          <Text style={styles.assessment}>
            1.Cardiovascular and Neurological
          </Text>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>i.Heart Rate: </Text>
          <Picker
            selectedValue={heartRate}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setHeartRate(itemValue)}
          >
            <Picker.Item label="Neonate and <215" value={0} />
            <Picker.Item label="Neonate and 215-225" value={3} />
            <Picker.Item label="Neonate and >225" value={4} />
            <Picker.Item label="Infant and <215" value={0} />
            <Picker.Item label="Infant and 215-225" value={3} />
            <Picker.Item label="Infant and >225" value={4} />
            <Picker.Item label="Child and <185" value={0} />
            <Picker.Item label="Child and 185-205" value={3} />
            <Picker.Item label="Child and >205 bpm" value={4} />
            <Picker.Item label="Adolescent and <145 bpm" value={0} />
            <Picker.Item label="Adolescent and 145-155 bpm" value={3} />
            <Picker.Item label="Adolescent and > 155 bpm" value={4} />
          </Picker>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>ii.GCS: </Text>
          <TextInput style={styles.input} onChangeText={setGcs} value={gcs} />
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>iii.Temperature: </Text>
          <Picker
            selectedValue={temperature}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setTemperature(itemValue)}
          >
            <Picker.Item label="<33" value={3} />
            <Picker.Item label="33-40" value={0} />
            <Picker.Item label=">40" value={3} />
          </Picker>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>iv.Pupillary Response: </Text>
          <Picker
            selectedValue={pupil}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setPupil(itemValue)}
          >
            <Picker.Item label="Both reactive" value={0} />
            <Picker.Item
              label="One reactive and (1 fixed and >3mm)"
              value={7}
            />
            <Picker.Item label="Both fixed and both > 3mm" value={11} />
          </Picker>
        </View>
        <Text style={styles.subscore}>
          Cardiovascular and Neurological subscore:{" "}
          {Number(gcs) +
            Number(heartRate) +
            Number(temperature) +
            Number(pupil)}
        </Text>
      </View>
      <View style={[styles.container2]}>
        <View>
          <Text style={styles.assessment}>2.Acid-base and blood gas</Text>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>i.Acidic pH: </Text>
          <Picker
            selectedValue={acidpH}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setAcidpH(itemValue)}
          >
            <Picker.Item label="pH >7.28 and CO2 >= 17" value={0} />
            <Picker.Item label="pH 7.0-7.28 or total CO2 5-16.9" value={2} />
            <Picker.Item label="pH <7.0 and total CO2 <5" value={6} />
          </Picker>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>ii. Alkaline pH: </Text>
          <Picker
            selectedValue={alkalinepH}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setAlkalinepH(itemValue)}
          >
            <Picker.Item label="<7.48" value={0} />
            <Picker.Item label="7.48-7.55" value={2} />
            <Picker.Item label=">7.55" value={3} />
          </Picker>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>iii.PCO2: </Text>
