            selectedValue={pco2}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setPco2(itemValue)}
          >
            <Picker.Item label="<50" value={0} />
            <Picker.Item label="50-75" value={1} />
            <Picker.Item label=">75" value={3} />
