            <Picker.Item label="Neonate and <=14.9" value={0} />
            <Picker.Item label="Neonate and >14.9" value={3} />
          </Picker>
        </View>
        <Text style={styles.subscore}>
          Biochemical Test subscore:{" "}
          {Number(serum) + Number(creatinine) + Number(plasma) + Number(urea)}
        </Text>
      </View>
      <View style={[styles.container4]}>
        <View>
          <Text style={styles.assessment}>4.Hematology Tests</Text>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>i.White Blood Cell count: </Text>
