            <Picker.Item label=">=3000" value={0} />
            <Picker.Item label="<3000" value={4} />
          </Picker>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>ii. Platelet Count: </Text>
