          </Picker>
        </View>
        <Text style={styles.subscore}>
          Haemotology Test subscore:{" "}
          {Number(wbc) + Number(platelet) + Number(ptt)}
        </Text>
      </View>
      <View style={[styles.container5]}>
        <View>
          <Text style={styles.assessment}>
            PRISM Score:{" "}
            {Number(gcs) +
