import { StyleSheet, Text, View,SafeAreaView, Picker, TextInput } from "react-native";
import React, { useState } from "react";

const Api = () => {
  const [sPrimary, setSPrimary] = useState("");
  const [sAsthma, setSAsthma] = useState("");
  const [sDermatitis, setSDermatitis] = useState("");
  const [sCold, setSCold] = useState("");
  const [sCirculation, setSCirculation] = useState("");
  const [sRhinitis, setSRhinitis] = useState("");
  const [mPrimary, setMPrimary] = useState("");
  const [mAsthma, setMAsthma] = useState("");
  const [mDermatitis, setMDermatitis] = useState("");
  const [mAeroallergen, setMAeroallergen] = useState("");
  const [mCold, setMCold] = useState("");
  const [mCirculation, setMCirculation] = useState("");
  const [mSensitization, setMSensitization] = useState("");

  let score;
  return (
    <SafeAreaView>
      <Text style={styles.prism}>Asthma Predictive Index (API)</Text>
      <View style={[styles.container1]}>
        <View>
          <Text style={styles.assessment}>
            1.Stringent API
          </Text>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>Primary: </Text>
          <Picker
            selectedValue={sPrimary}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setSPrimary(itemValue)}
          >
            <Picker.Item label="" value="" />
            <Picker.Item label="Yes" value="Yes" />
            <Picker.Item label="No" value="No" />
          </Picker>
        </View>
        <View style={styles.dropContainer}>
          <Text style={styles.dropdownContent}>Secondary: </Text>
          <Picker
            selectedValue={sPrimary}
            style={{ height: 50, width: 150 }}
            onValueChange={(itemValue, itemIndex) => setSPrimary(itemValue)}
          >
            <Picker.Item label="" value="" />
            <Picker.Item label="Yes" value="Yes" />
            <Picker.Item label="No" value="No" />
          </Picker>
        </View>

      </View>


      <View style={[styles.container5]}>
        <View>
          <Text style={styles.assessment}>
            PRISM Score:{" "}
            {Number(gcs) +
              Number(heartRate) +
              Number(temperature) +
              Number(pupil) +
              Number(acidpH) +
              Number(alkalinepH) +
              Number(pco2) +
              Number(pao2) +
              Number(totalco2) +
              Number(serum) +
              Number(creatinine) +
              Number(plasma) +
              Number(urea) +
              Number(wbc) +
              Number(platelet) +
              Number(ptt)}
          </Text>
        </View>
      </View>
    </SafeAreaView>
  );
};

export default Api;

const styles = StyleSheet.create({
  prism: {
    fontSize: 50,
    fontWeight: "bold",
    textAlign: "center",
  },
  assessment: {
    marginTop: 20,
    fontSize: 30,
    fontWeight: "bold",
  },
  dropContainer: {
    flexDirection: "row",
    marginBottom: 20,
    marginTop: 10,
  },
  dropdownContent: {
    fontSize: 20,
    marginTop: 10,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    width: 200,
  },
  text: {
    fontWeight: "bold",
  },
  container1: {
    backgroundColor: "#f0ffff",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container2: {
    backgroundColor: "#fffacd",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container3: {
    backgroundColor: "#ffb6c1",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container4: {
    backgroundColor: "#f5f5dc",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
  container5: {
    backgroundColor: "#dda0dd",
    paddingLeft: 20,
    borderWidth: 2,
    margin: 5,
  },
