    Modal,
    Text,
    Pressable,
    View,
    SafeAreaView,
    TextInput
} from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import axios from 'axios'
function ReviewDetails(props) {
    const { patient } = props;
    const [str, setStr] = useState("");

    const navigate = useNavigate();
    return (
        <View style={styles.card}>
            <View style={styles.cardContent}>
                <Text>Patient ID : {patient.P_id}</Text>
            </View>
            <View style={styles.cardContent}>
                <Text>Last Updated By : {patient.D_id}</Text>
            </View>
            <View style={styles.cardContent}>
                <Text>Last Updated on : {patient.Time}</Text>
                <Text >
                    <Pressable>
                        <Icon.Button
                            // name="profile"
                            style={styles.buttonOpen}
                            onPress={() => {
                                // setStr("/Patient_id/" + String(patient.P_ID))
                                navigate("/Patient_id/" + String(patient.P_id))
                            }}
                        >
                            Assessment
                        </Icon.Button>
                    </Pressable>
                </Text>
            </View>
        </View>
    );
}

const Reminder = (props) => {
    const [GCS_List, setGCS_List] = useState([]);
    const [GCS, setGCS] = useState([]);
    const [CRS_List, setCRS_List] = useState([]);
    const [CRS, setCRS] = useState([]);
    const [Prism_List, setPrism_List] = useState([]);
    const [Prism, setPrism] = useState([]);
    const [PSofa_List, setPSofa_List] = useState([]);
    const [PSofa, setPSofa] = useState([]);
    const [OI_List, setOI_List] = useState([]);
    const [OI, setOI] = useState([]);
    const [API_List, setAPI_List] = useState([]);
    const [API, setAPI] = useState([]);

    function dateCondition(a,b){
        var dd = Number(a.substring(0,2))
        var hh = Number(a.substring(17,19));
        var mm = Number(a.substring(20,22));
        var ss = Number(a.substring(23,25));

        var curr = new Date()
        var cDD = curr.getDate()
        var cHH = curr.getHours()
        var cMM = curr.getMinutes()
        var cSS = curr.getSeconds()

        if(b == "Triage"){
            return false
        }
        if(b == "API"){
            return true
        }
        if(b == "GCS" || b=="CRS" || b=="OI" || b=="API"){
           if(cDD == dd){
               if((cHH-hh)>4){
                   return true;
               }
               else if((cHH-hh)<4){
                   return false;
               }
               else{
                   if(cMM < mm){
                       return false;
                   }else{
                       return true;
                   }
               }
           }else{
               if(((24-hh)+cHH) > 4){
                   return true;
               }else if(((24-hh)+cHH) < 4){
                    return false;
               }else{
                   if(cMM < mm){
                       return false;
                   }else{
                       return true;
                   }
               }
           }
        }
        if(b == "PSofa"){
            if(cDD == dd){
                if((cHH-hh)>6){
                    return true;
                }
                else if((cHH-hh)<6){
                    return false;
                }
                else{
                    if(cMM < mm){
                        return false;
                    }else{
                        return true;
                    }
                }
            }else{
                if(((24-hh)+cHH) > 6){
                    return true;
                }else if(((24-hh)+cHH) < 6){
                     return false;
                }else{
                    if(cMM < mm){
                        return false;
                    }else{
                        return true;
                    }
                }
            }
         }
         if(b == "Prism"){
             if(cDD == dd){
                 return false;
             }
             else{
                 if(((24-hh)+cHH)>24){
                     return true;
                 }
                 else if(((24-hh)+cHH)<24){
                    return false;
                 }
                 else{
                     if(cMM < mm){
                         return false;
                     }else{
                         return true;
                     }
                 }
             }
         }
    }

    function GetLists(){
        axios
      .get("http://localhost:4000/get/GCS")
      .then((response) => {
        if ((response.data) != null ) {
            // console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(dateCondition(response.data[i].Time,"GCS"))
                {
                    temp.push(response.data[i])
                }
            }
            setGCS_List(temp)
            setGCS(temp)
        }
        else {
          alert("Unable to get GCS data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });



      axios
      .get("http://localhost:4000/get/CRS")
      .then((response) => {
        if ((response.data) != null ) {
            // console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(dateCondition(response.data[i].Time,"CRS"))// condition
                {
                    temp.push(response.data[i])
                }
            }
            setCRS_List(temp)
            setCRS(temp)
        }
        else {
          alert("Unable to get CRS data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });


      axios
      .get("http://localhost:4000/get/Prism")
      .then((response) => {
        if ((response.data) != null ) {
            // console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(dateCondition(response.data[i].Time,"Prism"))// condition
                {
                    temp.push(response.data[i])
                }
            }
            setPrism_List(temp)
            setPrism(temp)
        }
        else {
          alert("Unable to get Prism data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });

      axios
      .get("http://localhost:4000/get/PSofa")
      .then((response) => {
        if ((response.data) != null ) {
            // console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(dateCondition(response.data[i].Time,"PSofa"))// condition
                {
                    temp.push(response.data[i])
                }
            }
            setPSofa_List(temp)
            setPSofa(temp)
        }
        else {
          alert("Unable to get PSofa data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });

      axios
      .get("http://localhost:4000/get/OI")
      .then((response) => {
        if ((response.data) != null ) {
            // console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(dateCondition(response.data[i].Time,"OI"))// condition
                {
                    temp.push(response.data[i])
                }
            }
            setOI_List(temp)
            setOI(temp)
        }
        else {
          alert("Unable to get OI data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });

      axios
      .get("http://localhost:4000/get/API")
      .then((response) => {
        if ((response.data) != null ) {
            console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(dateCondition(response.data[i].Time,"API"))// condition
                {
                    temp.push(response.data[i])
                }
            }
            setAPI_List(temp)
            setAPI(temp)
        }
        else {
          alert("Unable to get API data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
    }
    useEffect(() => {
        if (localStorage.getItem('user_data') == null) {
            alert("Unauthorised Access")
            navigate("/")
        }

        GetLists();
    }, []);

    const navigate = useNavigate();



    // for fuzzy search
    // const [searchData, setSearchData] = useState(GCS);
    // const searchItem = (query) => {
    //     if (!query) {
    //         setGCS(GCS_List);
    //         return;
    //     }
    //     const finalResult = [];
    //     GCS_List.forEach((item) => {
    //         if (
    //             item.P_id.indexOf(query) !== -1
    //         ) {
    //             finalResult.push(item);
    //         }
    //     });
    //     setGCS(finalResult);
    // };

    return (
        <SafeAreaView >
            <View>
            <View >
                    <Text style={styles.text}> Following Patients requires reassessment for GCS:</Text>
                </View>
            {GCS_List.map((gcs) => {
                return < ReviewDetails key={gcs.P_id} patient={gcs} />
            })
            }
            </View>
            <View>
            <View >
                    <Text style={styles.text}>Following Patients requires reassessment for CRS:</Text>
                </View>
            {CRS_List.map((gcs) => {
                return < ReviewDetails key={gcs.P_id} patient={gcs} />
            })
            }
            </View>
            <View>
                <View >
