import { useNavigate } from "react-router-dom";
import React, { useState } from 'react';
import {
    StyleSheet,
    Text,
    TextInput,
    SafeAreaView,
    Pressable,
    Alert,
  } from 'react-native';

const [username, setUsername] = useState('');
const [password, setPassword] = useState('');

const Login = () => {
  const Navigate =useNavigate()
  const authenticate = () => {
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        username: username,
        password: password,
        hospitalID: "dpmta"
      })
    };

    fetch('https://devapi.igzolt.in/index.php/Api/login', requestOptions)
    .then(res => {
      res.json()
      .then(data => {
        console.log(data)
      })
    })
    .catch(e => {
      console.log(e);
    })
    if(username == "devteam" && password == "dev@123") {
      Navigate('/User_id')
    }
    else {
      Alert.alert("Invalid username or password");
    }
  }


    return (
        <SafeAreaView style={styles.container}>
          <TextInput 
            placeholder="Email"
            style={styles.inputStyle}
            onChangeText={(username) => {
              setUsername(username);
            }}
          />
          <TextInput
            secureTextEntry={true}
            placeholder="Password"
            style={styles.inputStyle}
            onChangeText={(password) => {
              setPassword(password);
            }}
          />
          <Pressable onPress={authenticate} style={styles.button}>
            <Text style={styles.buttonLabel}>Login</Text>
          </Pressable>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
    },
    formLabel: {
      fontSize: 20,
      color: '#fff',
    },
    inputStyle: {
      marginTop: 20,
      width: 300,
      height: 40,
      paddingHorizontal: 10,
      borderRadius: 50,
      backgroundColor: '#DCDCDC',
    },
    formText: {
      alignItems: 'center',
      justifyContent: 'center',
      color: '#fff',
      fontSize: 20,
    },
    text: {
      color: '#fff',
      fontSize: 20,
    },
    button: {
      marginTop: 20,
      paddingHorizontal: 10,
      // paddingVertical: 6,
      borderRadius: 50,
      // backgroundColor: 'oldlace',
      // marginHorizontal: '1%',
      // marginBottom: 6,
      // minWidth: '48%',
      width: 300,
      height: 40,
      // textAlign: 'center',
      alignItems: 'center',
    },
    buttonLabel: {
      fontSize: 20,
      fontWeight: 'bold',
      color: 'blue',
    },
});

export default Login;
