import { StyleSheet, Text, View,SafeAreaView, Picker, TextInput, Button, Pressable } from "react-native";
import React, { useState, useEffect } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import axios from 'axios';
function GetP_id() {

  var c = 0;
  var index = 0
  var path = location.pathname
  for (var i = 0; i < path.length; i++) {
    if (path.charAt(i) == '/') {
      c++;
    }
    if (c == 3) {
      index = i
      break;
    }
    if (c == 2) {
      index = path.length;
    }
  }
  return path.substring(12, index)
}
const Triage = () => {
  const [appearance, setAppearance] = useState("");
  const [breathing, setBreathing] = useState("");
  const [skin, setSkin] = useState("");
  const [airway, setAirway] = useState("")
  const [pentagonBreathing, setPentagonBreathing] = useState("")
  const [circulation, setCirculation] = useState("")
  const [disability, setDisability] = useState("")
  const [exposure, setExposure] = useState("")
  const [textairway,setTextAirway] = useState("")
  const [textbreath, onChangeBreath] = useState("")
  const [airEntry,setAirEntry] = useState("")
  const [auscultation,setAuscultation] = useState("")
  const [spo2Air, setSpo2Air] = useState("")
  const [spo2Fio2,setSpo2Fio2] = useState("")
  const [etco2,setEtco2] = useState("")
  const [hr,setHr] = useState("")
  const [cft,setCft] = useState("")
  const [bp,setBp] = useState("")
  const [central,setCentral] = useState("")
  const [peripheral,setPeripheral] = useState("")
  const [skinTemp,setSkinTemp] = useState("")
  const [rhythm,setRhythm] = useState("")
  const [wave,setWave] = useState("")
  const [others,setOthers] = useState("")
  const [gcs,setGcs] = useState("")
  const [pupil,setPupil] = useState("")
  const [reaction,setReaction] = useState("")
  const [motor,setMotor] = useState("")
  const [sugar,setSugar] = useState("")
  const [temperature,setTemperature] = useState("")
  const [color,setColor] = useState("")
  const [surface,setSurface] = useState("")
  const [otherExposure,setOtherExposure] = useState("")
  const [stable,setStable] = useState("")
  const [distress,setDistress] = useState("")
  const [failure,setFailure] = useState("")
  const [cshock,setCshock] = useState("")
  const [hshock,setHshock] = useState("")
  const [brain,setBrain] = useState("")
  const [cardioFailure,setCardioFailure] = useState("")
  const [cardioArrest,setCardioArrest] = useState("")
  const [level,setLevel] = useState("")
  const [istable,setIstable] = useState("")
  const [lifeThreat,setLifeThreat] = useState("")
  const [nolifeThreat,setNolifeThreat] = useState("")
  const [c, setC] = useState(1);
  const location = useLocation();
  let flag = 1

  const onClickAdd = (event) => {
    if (flag == 1) {
      setC(0);
      const newTriage = {
      "P_id":GetP_id(),
      "a":appearance,
      "b":breathing,
      "c":skin,
      "d":airway,
      "e": textairway,
      "f": textbreath,
      "g":breathing,
      "h":airEntry,
      "i":auscultation,
      "j":spo2Air,
      "k": spo2Fio2,
      "l": etco2,
      "m":hr,
      "n":cft,
      "o":bp,
      "p":central,
      "q": peripheral,
      "r": skinTemp,
      "s":rhythm,
      "t":breathing,
      "u":skin,
      "v":airway,
      "w": textairway,
      "x": textbreath,
      "y": textbreath,
      "z":appearance,
      "A":breathing,
      "B":skin,
      "C":airway,
      "D": textairway,
      "E": textbreath,
      "F":appearance,
      "F":breathing,
      "G":skin,
      "H":airway,
      "I": textairway,
      "J": textbreath,
      "K":appearance,
      "L":breathing,
      "M":skin,
      "N":airway,
      "O": textairway,
      "P": textairway

      };

      // console.log(newCrs)
      axios
        .post("http://localhost:4000/add/Triage", newTriage)
        .then((response) => {
          if (response.data != null) {
            alert("Added");
          } else {
            alert("Unable to add!");
          }
          // console.log(response.data);
        })
        .catch(function (error) {
          console.log(error);
        });
    } else {
      alert("Fill all the details");
    }
  };

  function GetAssessment() {
    axios
      .get("http://localhost:4000/get/PSofa/" + GetP_id())
      .then((response) => {
        if ((response.data) != null && response.data.length == 1) {
          console.log(response.data[0]);

          setSelectFlag(response.data[0].Resp_select);
          setRespiratory(response.data[0].Resp_Value);
          setPlatelet(response.data[0].PlatCount);
          setBilirubin(response.data[0].Bilirubin);
          setAge(response.data[0].Card_AgeGrp);
          setCardio(response.data[0].Card_MAP);
          setVaso(response.data[0].Card_Vaso);
          setGcs(response.data[0].GCS);
          setRenal(response.data[0].Renal)
        }
        else {
          alert("Unable to get previous data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }
  useEffect(() => {
    // console.log("api")
    GetAssessment();
  }, []);




  return (
    <SafeAreaView>
      <Text style={styles.triage}>Triage</Text>
      <View style={[styles.container1]}>
      <View>
        <Text style={styles.assessment}>
          1.General Assessment (Pediatric assessment Triangle)
        </Text>
      </View>
      <View style={styles.dropContainer}>
        <Text style={styles.dropdownContent}>Appearance: </Text>
        <Picker
          selectedValue={appearance}
          style={{ height: 50, width: 150 }}
          onValueChange={(itemValue, itemIndex) => setAppearance(itemValue)}
        >
          <Picker.Item label=" " value=" " />
          <Picker.Item label="Normal" value="Normal" />
          <Picker.Item label="Abnormal" value="Abnormal" />
        </Picker>
