import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from "react-router-dom";
import {
    Alert,
    Button,
    ImageBackground,
    StyleSheet,
    Modal,
    Text,
    Pressable,
    View,
    SafeAreaView,
    TextInput
} from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import axios from 'axios'
function ReviewDetails(props) {
    const { patient } = props;
    const [str, setStr] = useState("");

    const navigate = useNavigate();
    return (
        <View style={styles.card}>
            <View style={styles.cardContent}>
                <Text>Patient ID : {patient.P_id}</Text>
            </View>
            <View style={styles.cardContent}>
                <Text>Last Updated By : {patient.D_id}</Text>
            </View>
            <View style={styles.cardContent}>
                <Text>Last Updated on : {patient.Time}</Text>
                <Text >
                    <Pressable>
                        <Icon.Button
                            // name="profile"
                            style={styles.buttonOpen}
                            onPress={() => {
                                // setStr("/Patient_id/" + String(patient.P_ID))
                                navigate("/Patient_id/" + String(patient.P_id))
                            }}
                        >
                            Assessment
                        </Icon.Button>
                    </Pressable>
                </Text>
            </View>
        </View>
    );
}

const Reminder = (props) => {
    const [GCS_List, setGCS_List] = useState([]);
    const [GCS, setGCS] = useState([]);
    const [CRS_List, setCRS_List] = useState([]);
    const [CRS, setCRS] = useState([]);
    const [Triage_List, setTriage_List] = useState([]);
    const [Triage, setTriage] = useState([]);
    const [Prism_List, setPrism_List] = useState([]);
    const [Prism, setPrism] = useState([]);
    const [PSofa_List, setPSofa_List] = useState([]);
    const [PSofa, setPSofa] = useState([]);
    const [OI_List, setOI_List] = useState([]);
    const [OI, setOI] = useState([]);
    const [_List, set_List] = useState([]);
    const [, set] = useState([]);

    function GetLists(){
        axios
      .get("http://localhost:4000/get/GCS")
      .then((response) => {
        if ((response.data) != null ) {
            console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(response.data[i].Time )// condition
                {
                    temp.push(response.data[i])
                }
            }
            setGCS_List(temp)
            setGCS(temp)
        }
        else {
          alert("Unable to get GCS data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });



      axios
      .get("http://localhost:4000/get/CRS")
      .then((response) => {
        if ((response.data) != null ) {
            console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(response.data[i].Time )// condition
                {
                    temp.push(response.data[i])
                }
            }
            setCRS_List(temp)
            setCRS(temp)
        }
        else {
          alert("Unable to get CRS data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });

      axios
      .get("http://localhost:4000/get/Triage")
      .then((response) => {
        if ((response.data) != null ) {
            console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(response.data[i].Time )// condition
                {
                    temp.push(response.data[i])
                }
            }
            setTriage_List(temp)
            setTriage(temp)
        }
        else {
          alert("Unable to get Triage data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });

      axios
      .get("http://localhost:4000/get/Prism")
      .then((response) => {
        if ((response.data) != null ) {
            console.log(response.data)
            let temp=[]
            for(var i =0;i<response.data.length;i++)
            {
                if(response.data[i].Time )// condition
                {
                    temp.push(response.data[i])
                }
            }
            setPrism_List(temp)
            setPrism(temp)
        }
        else {
          alert("Unable to get Prism data!");

        }
        // console.log(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
    }
    useEffect(() => {
        if (localStorage.getItem('user_data') == null) {
            alert("Unauthorised Access")
            navigate("/")
        }

        GetLists();
    }, []);

    const navigate = useNavigate();



    // for fuzzy search
    // const [searchData, setSearchData] = useState(GCS);
    // const searchItem = (query) => {
    //     if (!query) {
    //         setGCS(GCS_List);
    //         return;
    //     }
    //     const finalResult = [];
    //     GCS_List.forEach((item) => {
    //         if (
    //             item.P_id.indexOf(query) !== -1
    //         ) {
    //             finalResult.push(item);
    //         }
    //     });
    //     setGCS(finalResult);
    // };

    return (
        <SafeAreaView >
            <View>
                <Text>GCS</Text>
            {GCS_List.map((gcs) => {
                return < ReviewDetails key={gcs.P_id} patient={gcs} />
            })
            }
            </View>
            <View>
                <Text>CRS</Text>
            {CRS_List.map((gcs) => {
                return < ReviewDetails key={gcs.P_id} patient={gcs} />
            })
            }
            </View>
            <View>
                <Text>Triage</Text>
            {Triage_List.map((gcs) => {
                return < ReviewDetails key={gcs.P_id} patient={gcs} />
            })
            }
            </View>
            <View>
                <Text>Prism</Text>
            {Prism_List.map((gcs) => {
                return < ReviewDetails key={gcs.P_id} patient={gcs} />
            })
            }
            </View>
        </SafeAreaView >
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingTop: 5,
        paddingBottom: 5,
    },
    container1: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'flex-end',
        // paddingTop: 5,
        // paddingBottom: 5,
    },
    centeredView: {
        flex: 1,
