        padding: 4,
        // borderRadius: 30
    },
    input: {
        height: 40,
        margin: 12,
        borderWidth: 1,
        padding: 5,
    },
    buttonfuzzySearch: {
        borderWidth: 2,
        borderRadius: 8,
        borderColor: "#56BF9C",
        strokeWidth: "1"
    },
    searchIcon: {
        padding: 5
    },
    card: {
        borderRadius: 6,
        elevation: 3,
